function getDays(){let a=new Date,t=a.getMonth()+1,e=a.getFullYear(),s=new Date(e,t,0).getDate(),r=[];for(var o=0;o<=s;o++)r.push(o);return r}$.ajax({url:transaksi_hariUrl,type:"get",dataType:"json",success:a=>{$("#transaksi_hari").html(a.total)}}),$.ajax({url:transaksi_terakhirUrl,type:"get",dataType:"json",success:a=>{$("#transaksi_terakhir").html(a)}}),$.ajax({url:stok_hariUrl,type:"get",dataType:"json",success:a=>{$("#stok_hari").html(a.total)}}),$.ajax({url:produk_terlarisUrl,type:"get",dataType:"json",success:a=>{var t=$("#produkTerlaris").get(0).getContext("2d");new Chart(t,{type:"pie",data:{labels:a.label,datasets:[{backgroundColor:["#f56954","#00a65a","#f39c12","#00c0ef","#3c8dbc","#d2d6de"],data:a.data}],options:{maintainAspectRatio:!1,responsive:!0}}})}}),$.ajax({url:data_stokUrl,type:"get",dataType:"json",success:a=>{$.each(a,(a,t)=>{let e=`<li class="list-group-item">\n                ${t.nama_produk}\n                <span class="float-right">${t.stok}</span>\n            </li>`;$("#stok_produk").append(e)})}}),$.ajax({url:penjualan_bulanUrl,type:"post",data:{day:getDays()},dataType:"json",success:a=>{var t=$("#bulanIni").get(0).getContext("2d");new Chart(t,{type:"bar",data:{labels:getDays(),datasets:[{label:"Total",backgroundColor:"rgba(60,141,188,0.9)",borderColor:"rgba(60,141,188,0.8)",pointRadius:!1,pointColor:"#3b8bba",pointStrokeColor:"rgba(60,141,188,1)",pointHighlightFill:"#fff",pointHighlightStroke:"rgba(60,141,188,1)",data:a}],options:{maintainAspectRatio:!1,responsive:!0,legend:{display:!1},scales:{xAxes:[{gridLines:{display:!1}}],yAxes:[{gridLines:{display:!1}}]}}}})},error:a=>{console.log(a)}});