function reloadTable() {
	laporan_penjualan.ajax.reload();
}
function remove(a) {
	Swal.fire({
		title: "Hapus",
		text: "Hapus data ini?",
		type: "warning",
		showCancelButton: !0,
	}).then(() => {
		$.ajax({
			url: deleteUrl,
			type: "post",
			dataType: "json",
			data: { id: a },
			success: () => {
				Swal.fire("Sukses", "Sukses Menghapus Data", "success"), reloadTable();
			},
			error: (a) => {
				console.log(a);
			},
		});
	});
}
