$('#form').submit(function(e){
	e.preventDefault(); 
	var url="";
	if($('[name="id"]').val()=="00"){
		url = addUrl;
	}else{
		url = editUrl
	}
		 $.ajax({
			 url:url,
			 type:"post", //method Submit
			 data:new FormData(this), //penggunaan FormData
			 processData:false,
			 contentType:false,
			 cache:false,
			 async:false,
			 success: (a) => {
				$(".modal").modal("hide"),
					Swal.fire("Sukses", "Sukses Menambahkan Data", "success"),
					reloadTable();
			},
			error: (a) => {
				console.log(a);
			},
		 });
});
function remove(e) {
	Swal.fire({
		title: "Hapus",
		text: "Hapus data ini?",
		type: "warning",
		showCancelButton: !0,
	}).then(() => {
		$.ajax({
			url: deleteUrl,
			type: "post",
			dataType: "json",
			data: { id: e },
			success: () => {
				Swal.fire("Sukses", "Sukses Menghapus Data", "success"), reloadTable();
			},
			error: () => {
				console.log(a);
			},
		});
	});
}
function editData() {
	$.ajax({
		url: editUrl,
		type: "post",
		dataType: "json",
		data: $("#form").serialize(),
		success: () => {
			$(".modal").modal("hide"),
				Swal.fire("Sukses", "Sukses Mengedit Data", "success"),
				reloadTable();
		},
		error: (a) => {
			console.log(a);
		},
	});
}
function add() {
	$('[name="id"]').val("00")
	(url = "add"),
		$(".modal-title").html("Add Data"),
		$('.modal button[type="submit"]').html("Add");
}
function edit(a) {
	$.ajax({
		url: getProdukUrl,
		type: "post",
		dataType: "json",
		data: { id: a },
		success: (a) => {
			$('[name="id"]').val(a.id_menu),
				$('[name="nama"]').val(a.nama_menu),
				$('#kategori').val(a.id_kategori),
				$('[name="harga"]').val(a.harga),
				$(".modal").modal("show"),
				$(".modal-title").html("Edit Data"),
				$('.modal button[type="submit"]').html("Edit"),
				(url = "edit");
				$('#kategori').select2().trigger('change');
		},
		error: (a) => {
			console.log(a);
		},
	});
}
produk.on("order.dt search.dt", () => {
	produk
		.column(0, { search: "applied", order: "applied" })
		.nodes()
		.each((a, e) => {
			a.innerHTML = e + 1;
		});
}),
	$("#form").validate({
		errorElement: "span",
		errorPlacement: (a, e) => {
			a.addClass("invalid-feedback"), e.closest(".form-group").append(a);
		},
		submitHandler: () => {
			"edit" == url ? editData() : addData();
		},
	}),
	$("#kategori").select2({
		placeholder: "Kategori",
	}),
	$("#status").select2({
		placeholder: "Satuan",
	}),
	$(".modal").on("hidden.bs.modal", () => {
		$("#form")[0].reset(), $("#form").validate().resetForm();
	});
